import axios from 'axios';

const apiUrl = "http://localhost:5141"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`)    
    return result.data;
  },

  addTask: async (name) => {
    try {
      console.log('addTask', name);
      // Send a POST request to add a new task
      const result = await axios.post(`${apiUrl}/tasks`, { name, isComplete: false });
      return result.data; // Return the created task
    } catch (error) {
      console.error('Error adding task:', error);
      throw error;
    }
  },
  
  // Set task completion status
  setCompleted: async (id, isComplete) => {
    try {
      console.log('setCompleted', { id, isComplete });
      // Send a PUT request to update task completion status
      const result = await axios.put(`${apiUrl}/tasks/${id}`, { isComplete });
      return result.data; // Return the updated task
    } catch (error) {
      console.error('Error setting task completion status:', error);
      throw error;
    }
  },
  
  // Delete a task
 deleteTask: async (id) => {
    try {
      console.log('deleteTask', { id });
      // Send a DELETE request to remove the task
      await axios.delete(`${apiUrl}/tasks/${id}`);
      console.log('Task deleted');
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
    }
  }

};
